sp_widget.client_script:
function updateClock() {
  var now = new Date();
  var hours = now.getHours() % 12;
  var minutes = now.getMinutes();
  var seconds = now.getSeconds();

  var hourHand = g_form.getControl('hour-hand');
  var minuteHand = g_form.getControl('minute-hand');
  var secondHand = g_form.getControl('second-hand');

  var hourRotation = (hours * 30) + (minutes * 0.5);
  var minuteRotation = (minutes * 6) + (seconds * 0.1);
  var secondRotation = seconds * 6;

  hourHand.style.transform = 'rotate(' + hourRotation + 'deg)';
  minuteHand.style.transform = 'rotate(' + minuteRotation + 'deg)';
  secondHand.style.transform = 'rotate(' + secondRotation + 'deg)';

  setTimeout(updateClock, 1000); // Update every second
}

updateClock();

sp_widget.css:
