sp_widget.client_script:
function expandCard(card) {
  card.classList.toggle("expanded");
}

var cards = document.querySelectorAll(".flex-card");

cards.forEach(function(card) {
  card.addEventListener("click", function() {
    expandCard(this);
  });
});

sp_widget.css:
.flex-cards {
  display: flex;
  justify-content: center;
  align-items: center;
}

.flex-card {
  width: 1000px;
  margin: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
  overflow: hidden;
  box-shadow: 0 0 5px rgba(0, 0, 0, 0.2);
}

.card-header {
  position: relative;
  overflow: hidden;
}

.card-image {
  width: 100%;
  height: auto;
}

.card-content {
  padding: 10px;
}

.card-title {
  margin-top: 0;
}

.card-description {
  margin-bottom: 0;
  color: #777;
  font-size: 14px;
}

.expanded {
  width: 400px;
  cursor: pointer;
}

.expanded .card-image {
  height: 400px;
}

.expanded .card-content {
  display: none;
}
