sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

 
var sysId = $sp.getParameter('sysId');

data.records = [];

    var itemGr = new GlideRecord('x_1046400_auction_all_item');
		itemGr.addQuery('auctioned_on',sysId);
    itemGr.query();

    while(itemGr.next()){
         var record = {};
        record.name = itemGr.getValue('number');
        record.image = itemGr.getValue('image');
        record.description = itemGr.getValue('description');        
        record.category= itemGr.getValue('category');
        record.startingbid = itemGr.getValue('starting_bid');
        record.sysId = itemGr.getValue('sys_id');

        data.records.push(record);
    } 
	})();
