sp_widget.script:
(function() {
  var sysId = $sp.getParameter('sysId');

  data.records = [];

  var itemGr = new GlideRecord('x_1046400_auction_all_item');
  itemGr.addQuery('auctioned_on', sysId);
  itemGr.query();

  while (itemGr.next()) {
    var record = {};
    record.name = itemGr.getValue('number');
    record.image = itemGr.getValue('image');
    record.description = itemGr.getValue('description');
    record.category = itemGr.getValue('category');
    record.startingbid = itemGr.getValue('starting_bid');
    record.sysId = itemGr.getValue('sys_id');

    var auctionTime;

    var gr = new GlideRecord('x_1046400_auction_auction');
		gr.get(itemGr.getValue('auctioned_on'));
    gr.query();
    if (gr.next()) {
      auctionTime = gr.getValue('end_time');
    }


    var currentTime = new GlideDateTime();


    record.disableBidButton = auctionTime <= currentTime;

		console.log("disableBidButton");

    data.records.push(record);
		console.log("disableBidButton" + record.disableBidButton);
  }
})();
