sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

 


data.records = [];

    var itemGr = new GlideRecord('x_1046400_auction_all_item');

    itemGr.query();

    while(itemGr.next()){
         var record = {};
        record.column1 = itemGr.getValue('name');
        record.column2 = itemGr.getValue('image');
        record.column3 = itemGr.getValue('description');        
        record.column4 = itemGr.getValue('ranking');
        record.column5 = itemGr.getValue('starting_bid');

        data.records.push(record);
    } 
	})();
