sp_widget.client_script:
api.controller=function() {
  // Get the start time from the record or any other source
  var startTime = g_form.getValue('start_time'); // Replace <start_time_field> with the actual field name

  // Convert the start time to a GlideDateTime object
  var startDateTime = new GlideDateTime();
  startDateTime.setDisplayValue(startTime);

  // Get the current time as a GlideDateTime object
  var currentDateTime = new GlideDateTime();

  // Calculate the remaining time in milliseconds
  var remainingTime = startDateTime.getNumericValue() - currentDateTime.getNumericValue();

  // Update the countdown timer every second
  setInterval(function() {
    remainingTime -= 1000; // Subtract 1 second

    // Convert remaining time to hours, minutes, and seconds
    var hours = Math.floor(remainingTime / (1000 * 60 * 60));
    var minutes = Math.floor((remainingTime % (1000 * 60 * 60)) / (1000 * 60));
    var seconds = Math.floor((remainingTime % (1000 * 60)) / 1000);

    // Display the countdown timer
    var countdownElement = document.getElementById('countdown');
    countdownElement.innerHTML = hours + 'h ' + minutes + 'm ' + seconds + 's';

    // Check if the countdown has reached zero
    if (remainingTime <= 0) {
      clearInterval(); // Stop the interval when the countdown reaches zero
      countdownElement.innerHTML = 'Countdown complete!';
    }
  }, 1000); // Update the countdown every second (1000 milliseconds)


};

