sp_widget.client_script:
api.controller=function($scope) {
	/* widget controller */
	var c = this;
	c.errMsg = null;
	c.sucMsg = null;
	$scope.currency = function(){
		c.errMsg = null;
		c.sucMsg = null;

		c.server.update().then(function(response){

			c.errMsg = response.errorMsg;
			c.sucMsg = response.sucMsg;
		});
	}
};

