sp_widget.script:
(function() {
	var c = this;
	var userId = gs.getUserID();
	data.records = [];

	var grUser = new GlideRecord('sys_user');
	grUser.addQuery('sys_id', userId);
	grUser.query();

	if (grUser.next()) {
		var loggedInUser = grUser.getValue('name');
		var itemGr = new GlideRecord('x_1046400_auction_all_item');
		itemGr.addQuery('wonby', loggedInUser);
		itemGr.query();

		while (itemGr.next()) {
			var record = {};
			record.name = itemGr.getValue('name');
			record.image = itemGr.getValue('image');
			record.bid = itemGr.getValue('u_currency_1');
			data.records.push(record);

		}
	}
})();
