sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

 


data.records = [];

    var itemGr = new GlideRecord('x_1046400_auction_auction');
    itemGr.query();

    while(itemGr.next()){
         var record = {};
        record.column1 = itemGr.getValue('name');
        record.column2 = itemGr.getValue('start_time');
        record.column3 = itemGr.getValue('end_time');        
        record.column4 = itemGr.getValue('appraiser');
        

        data.records.push(record);
    } 
	})();
