sys_script_client.script:
function onLoad() {
  var numberField = 'name'; // Modify this field name according to your table schema

  if (g_form.isNewRecord()) {
    var gr = new GlideRecord(g_form.getTableName('x_1046400_auction_auction'));
    gr.addQuery(numberField, 'ISNOTEMPTY');
    gr.orderByDesc(numberField);
    gr.setLimit(1);
    gr.query();

    var sequenceNumber = 1; // Set the initial sequence number

    if (gr.next()) {
      var lastNumber = gr.getValue(numberField);
      sequenceNumber = parseInt(lastNumber, 10) + 1;
    }

    // Pad the sequence number with leading zeros if necessary
    var paddedNumber = String(sequenceNumber).padStart(3, '0');

    // Assign the sequence number to the name field
    g_form.setValue(numberField, paddedNumber);
  }
}
